import React from "react";
import "./style.scss";
import Popup from "./popup";
import { search } from "../../actions/search_action";
import { searchUser } from "../../actions/user_action";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import faSearch from "@fortawesome/fontawesome-free-solid/faSearch";
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";
import {
  Settings,
  Dots,
  Heart,
  Pencil,
  Phone,
  Photo,
  Sticker,
  Send,
  Ghost,
  Edit,
  Circle,
  CircleCheck,
  User,
  Users,
} from "tabler-icons-react";
import { default as SearchBar } from "tabler-icons-react/dist/icons/search";
import { ClickAwayListener } from "@material-ui/core";

class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      errors: [],
      isError: false,
      result: [],
      tags: [],
      foundFoods: [],
      showResult: false,
      keyword: "",
    };
  }

  search(e) {
    if (e.target.value == "") {
      this.setState({
        showResult: false,
        result: [],
      });
    } else {
      searchUser(e.target.value).then((response) => {
        this.setState({
          result: [...response.users],
          tags: [...response.tags],
          showResult: true,
        });
      });
    }
  }

  searchHandle(e) {
    e.preventDefault();
    return false;
    // console.log(e.target[0]);
    // const keyword = e.target[0].value;
    // this.props.dispatch(search(keyword)).then((response) => {
    //     this.props.history.push(`/search/${keyword}`)
    // })
  }

  render() {
    const { result, showResult, tags } = this.state;
    return (
      <div className="search">
        <ClickAwayListener
          onClickAway={() => this.setState({ showResult: false })}
        >
          <div className="search-container">
            <div className="content">
              <form
                className="comment_form"
                onSubmit={(e) => this.searchHandle(e)}
              >
                <div className="wrapper">
                  <SearchBar size={22} strokeWidth={1} color="grey"></SearchBar>
                  <input
                    type="text"
                    placeholder="Search..."
                    onChange={(e) => {
                      this.search(e);
                    }}
                  />
                </div>
              </form>
              <Popup isOpen={showResult} items={result} tags={tags} />
            </div>
          </div>
        </ClickAwayListener>
      </div>
    );
  }
}
export default connect()(withRouter(Search));
